import React, {useEffect, useRef, useState} from 'react';
import axios from "axios";
import {useLocation} from "react-router-dom";
import "./CarPage.css"

function CarPage() {
    const useDidMountEffect = (func, deps) => {
        const didMount = useRef(false);
        useEffect(() => {
            if (didMount.current) func();
            else didMount.current = true;
        }, deps);
    }

    const location = useLocation();
    const names = ["Брэнд", "Модель"]
    const [img, setImg] = useState("")
    const [carData, setCarData] = useState({})
    useEffect(() => {
        console.log(location.pathname.split('/').pop())
        axios("http://localhost:8888/cars/get-car-by-id", {
            params: {
                id: location.pathname.split('/').pop()
            }
        }).then(it => setCarData(it.data))
    }, [])

    useDidMountEffect(() => {
        setImg(JSON.parse(carData.photo))
        console.log(carData)
    }, [carData])

    return (
        <div className={"car-page-container"}>
        <span className={"name"}>{carData.name}</span>
        <span className={"price"}>{`${carData.price} ₽`}</span>
        <div className={"image-container"}>
            <img className={"car-image"} src={`http://localhost:8888/uploads/${img}`} alt=""/>

            <div>Описание: </div>
            <div>{carData.description}</div>
            <ul>
                <div>{}</div>
                <div></div>
                <div></div>
                <div></div>
            </ul>
        </div>

        </div>
    );
}

export default CarPage;
