import React from 'react';
import {Button, Form, FormControl} from "react-bootstrap";
import './AdForm.css'
import { useForm } from "react-hook-form";
import { ErrorMessage } from '@hookform/error-message';

function AdForm(props) {
    const fields = {}
    const {register, handleSubmit, formState: { errors }, formState, watch} = useForm()
    const onSubmit = (data) => {
        console.log(data)
    }
    const onError = (errors) => {
        console.log(errors)
    }
    console.log(watch())
    console.log(formState.errors)

    return (
        <Form className={"ad-form"} onSubmit={handleSubmit(onSubmit, onError)}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <div>Введите параметры авто</div>
                <Form.Label>Email address</Form.Label>
                <ErrorMessage errors={errors} name="someField" />
                <FormControl type={"text"} {...register('someField', {minLength : {value: 6, message: "Длина короткая - минимум 6"}}
                )}
                 placeholder="Enter email" />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control {...register('myForm', {required: "Пожалуйста введите пароль"})} type="password" placeholder="Password" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit">
                Опубликовать
            </Button>
        </Form>
    );
}

export default AdForm;
