import React from 'react';
import {Button, Form, FormControl} from "react-bootstrap";
import './AdForm.css'
import { useForm } from "react-hook-form";
import { ErrorMessage } from '@hookform/error-message';


function AdForm(props) {
    const fields = {}
    const {register, handleSubmit, formState: { errors }, formState, watch} = useForm()
    console.log(watch())
    console.log(formState.errors)
    const firstField = {...register('lol', {minLength : 2})}

    return (
        <Form className={"ad-form"}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>

                <FormControl onChange={firstField.onChange} name={"name"} type={"text"}
                 placeholder="Enter email" />
                <ErrorMessage errors={errors} name={"name"}
                              render={({ message }) => <div className='has-error'>{message}</div>}
                />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control {...register('myForm',
                    {required: "Please enter your first name", maxLength: 2 }
                )} type="password" placeholder="Password" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    );
}

export default AdForm;
