import React, {useEffect, useRef, useState} from 'react';
import {Button, Form, FormControl} from "react-bootstrap";
import './AdForm.css'
import { useForm } from "react-hook-form";
import { ErrorMessage } from '@hookform/error-message';


function AdForm(props) {

    const useDidMountEffect = (func, deps) => {
        const didMount = useRef(false);
        useEffect(() => {
            if (didMount.current) func();
            else didMount.current = true;
        }, deps);
    }
    let [characteristicsFlag, setCharacteristicsFlag] = useState(false)
    const [allOptions, setAllOptions] = useState([])
    const [deleteOption, setDeleteOption] = useState(0)

    useDidMountEffect(() => {
        console.log(allOptions)
        setAllOptions(allOptions.filter(item => item.key !== deleteOption))
    },[deleteOption])


    const handleDelete = (id) => {
        console.log("allOptions",allOptions)
        console.log("id",id)
        const items = allOptions.filter(item => item.key !== id);
        setAllOptions(items)
    }


    useDidMountEffect(() => {
        console.log(allOptions)

    }, [allOptions.length])


    const displayCharacteristics = () => {setCharacteristicsFlag(!characteristicsFlag)}
    useDidMountEffect(() => {
    }, [characteristicsFlag])
    useEffect(() => {
    }, [])

    const {register, handleSubmit, formState: { errors }, formState, watch} = useForm()
    const onSubmit = (data) => {console.log(data)}
    const onError = (errors) => {console.log(errors)}


    // console.log(watch())
    // console.log(formState.errors)

    return (
        <Form className={"ad-form"} onSubmit={handleSubmit(onSubmit, onError)}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <div className={"form-title"}>Заполните предложенные поля</div>
                <Form.Label>Название авто</Form.Label>
                <ErrorMessage errors={errors} name="name" />
                <FormControl type={"text"} {...register('name', {minLength : {value: 6, message: "Длина короткая - минимум 6"}})}
                 placeholder="Введите название авто" />
                <Form.Text className="text-muted">
                    *не более 40 символов (например: Chevrolet lacetti).
                </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Описание</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control as="textarea" {...register('Описание', {required: "Пожалуйста введите пароль"})} type="text" placeholder="Введите описание" />
                <Form.Text className="text-muted">
                    *не более 256 символов.
                </Form.Text>
            </Form.Group>

            <Form.Group controlId="formFile" className="mb-3">
                <Form.Label>Загрузите фотографии или изображения</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <div>
                    <Form.Control  type="file" multiple/>
                </div>
                <Form.Text className="text-muted">
                    *не более 5 штук, допустимые форматы .jpg .png .jpeg.
                </Form.Text>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Номер телефона</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите телефон" />
                <Form.Text className="text-muted">
                    *в международном формате (+7).
                </Form.Text>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Цена продажи в рублях</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите цену" />
                <Form.Text className="text-muted">
                    *целое количество
                </Form.Text>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check onChange={() => displayCharacteristics()} type="checkbox" label="Указать технические характеристики машины" />
            </Form.Group>
            <Form.Group className={`mb-3 ${characteristicsFlag ? "slider" : "slider slider-closed"}`}>
                <Form.Label>Марка машины</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите марку" />
            </Form.Group>
            <Form.Group className={`mb-3 ${characteristicsFlag ? "slider" : "slider slider-closed"}`}>
                <Form.Label>Модель машины</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите модель" />
            </Form.Group>
            <Form.Group className={`mb-3 ${characteristicsFlag ? "slider" : "slider slider-closed"}`}>
                <Form.Label>Год производства</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите год производства" />
            </Form.Group>
            <Form.Group className={`mb-3 ${characteristicsFlag ? "slider" : "slider slider-closed"}`}>
                <Form.Label>Тип кузова</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите тип кузова" />
            </Form.Group>
            <Form.Group className={`mb-3 ${characteristicsFlag ? "slider" : "slider slider-closed"}`}>
                <Form.Label>Пробег</Form.Label>
                <ErrorMessage errors={errors} name="myForm" />
                <Form.Control  placeholder="Введите пробег" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <span><Button onClick={() => {
                    const id = Math.floor(Math.random() * 9999)
                    setAllOptions([
                        ...allOptions,
                        <Form.Group className={`mb-3`} key = {id}>
                            <span><Button className={"add-option"} onClick={() => {setDeleteOption(id)}}>-</Button></span>
                            <Form.Label>Дополнительная опция</Form.Label>
                            <ErrorMessage errors={errors} name="myForm" />
                            <span><Form.Control className={"additional-option"}  placeholder="Введите дополнительную опцию" /></span>
                        </Form.Group>
                    ])
                }} className={"add-option"}>+</Button></span>
                <span>Добавить дополнительную опцию</span>
            </Form.Group>
            {allOptions}
            <Button  variant="primary" type="submit">
                Опубликовать
            </Button>
        </Form>
    );
}

export default AdForm;
