import React, {useEffect, useState, useRef} from 'react';
import Pages from "./Pagination/Pages";
import "./CarsPageStyle.css"
import CarCard from "./CarsCard/CarsCard";
import axios from "axios";
import {Button, Card} from "react-bootstrap";
import CarsCard from "./CarsCard/CarsCard";

function CarsPage(props) {
    const useDidMountEffect = (func, deps) => {
        const didMount = useRef(false);
        useEffect(() => {
            if (didMount.current) func();
            else didMount.current = true;
        }, deps);
    }

    const [cars, setCars] = useState([]);
    const [totalCount, setTotalCount] = useState(undefined)
    const [carsCards, setCarsCards] = useState([])
    let [curPage, setCurPage] = useState(1)
    let countOfPages = 0
    let perPage = 5;

    useDidMountEffect(() => {
        console.log('cars', cars)
        let cards = cars.map((it) => <CarsCard key={it.id} name={it.name} price={it.price}/>)
        setCarsCards(cards)
    }, [cars.length])

    useDidMountEffect(() => {
        let cards = cars.map((it) => <CarsCard key={it.id} name={it.name} price={it.price}/>)
        carsCards.push(cards);
    }, [curPage])

    useEffect(() => {
        axios.get("http://localhost:8888/cars/get-car-page", {
            params: {
                'page': 1,
                'per-page': 5
            }
        }).then(it => {
                setCars(it.data.items)
                setTotalCount(it.data.totalCount)
            }
        )
    }, [])

    useDidMountEffect(() => {
        countOfPages = totalCount / perPage
    }, [totalCount])

    return (
    <div className={"main-container"}>
        <div className={"cars-content"}>
            <div className="container cars-container">
                <div className="row">
                    {carsCards}
                </div>
            </div>
            <div className={"btn-wrapper"}><Button className={"btn-next"} onClick={ () =>
            {
                if(!curPage === countOfPages) setCurPage(curPage + 1)
                axios.get("http://localhost:8888/cars/get-car-page", {
                    params: {
                        'page': curPage,
                        'per-page': perPage
                    }
                }).then(it => {
                        setCars(it.data.items)
                    }
                )
            }}>Показать еще</Button></div>
            <Pages />
        </div>
    </div>
    );
}

export default CarsPage;
