import React, {useEffect, useRef, useState} from 'react';
import axios from "axios";
import {useLocation} from "react-router-dom";
import "./CarPage.css"

function CarPage() {
    const useDidMountEffect = (func, deps) => {
        const didMount = useRef(false);
        useEffect(() => {
            if (didMount.current) func();
            else didMount.current = true;
        }, deps);
    }

    const location = useLocation();
    const names = ["Брэнд", "Модель", "Год производства", "Тип кузова", "Общий пробег"]
    const [img, setImg] = useState("")
    const [carData, setCarData] = useState()
    const [options, setOptions] = useState()
    const [car, setCar] = useState()
    useEffect(() => {
        console.log(location.pathname.split('/').pop())
        axios("http://localhost:8888/cars/get-car-by-id", {
            params: {
                id: location.pathname.split('/').pop()
            }
        }).then(it => setCarData(it.data))
    }, [])

    useDidMountEffect(() => {
        //setImg(JSON.parse(carData.photo))
        let options = carData.map(it => it.option_name)
        setImg(carData[0].photo)
        setCar(carData[0])
        setOptions(options)
    }, [carData])

    return (
        <></>
        // <div className={"car-page-container"}>
        // <span className={"name"}>{carData.name}</span>
        // <span className={"price"}>{`${carData.price} ₽`}</span>
        // <div className={"image-container"} style={{backgroundImage: `url("http://localhost:8888/uploads/${img}")`}}>
        // </div>
        //     {names.map((name, index) => {
        //     })}
        //     <div className={"field-name top-name"}>Описание: </div>
        //     <ul>
        //     </ul>
        //     <div className={"description"}>{carData.description}</div>
        //
        //
        // </div>
    );
}

export default CarPage;
